var page = new FullPage("#main", {
    onLeave: function (e) {
        // console.log(e)
        const navbarItems = document.querySelectorAll('.navbar-links .navbar-item .navbar-link');
        const navbarToggle = document.querySelectorAll('#navbar-toggle .icon-bar')

        console.log(navbarToggle);

        navbarToggle.forEach(item => {
            item.style.backgroundColor = (e.index) % 2 == 0?"white":"#2b2b2b";
        })

        navbarItems.forEach(item => {
            item.style.color = (e.index) % 2 == 0?"white":"#2b2b2b";
        })
    },
    afterLoad: function (e) {
        // console.log(e)
    }
});
